public:
    int maxArea(vector&lt;int&gt;&amp; height) {
        int n=height.size();
        int left=0,right=n-1;
        int res=0;
        while(left&lt;right){
            int c=right-left;
            int h=min(height[left],height[right]);
            int s=c*h;
            if(s&gt;res) res=s;
            if(height[left]&lt;=height[right]) left++;
            else right--;
        }
        return res;
    }
};
