using namespace std;
class MaxHeap {
private:
    vector&lt;int&gt; heap;

    void heapifyUp(int index) {
        while (index &gt; 0) {
            int parent = (index - 1) / 2;
            if (heap[index] &gt; heap[parent]) {
                swap(heap[index], heap[parent]);
                index = parent;
            } else {
                break;
            }
        }
    }

    void heapifyDown(int index) {
        int size = heap.size();
        while (index &lt; size) {
            int left = 2 * index + 1;
            int right = 2 * index + 2;
            int largest = index;

            if (left &lt; size &amp;&amp; heap[left] &gt; heap[largest]) {
                largest = left;
            }
            if (right &lt; size &amp;&amp; heap[right] &gt; heap[largest]) {
                largest = right;
            }
            if (largest != index) {
                swap(heap[index], heap[largest]);
                index = largest;
            } else {
                break;
            }
        }
    }

public:
    void insert(int x) {
        heap.push_back(x);
        heapifyUp(heap.size() - 1);
    }

    int getMax() {
        if (!heap.empty()) {
            return heap[0];
        }
        throw runtime_error("Heap is empty");
    }

    void deleteMax() {
        if (heap.empty()) {
            throw runtime_error("Heap is empty");
        }
        heap[0] = heap.back();
        heap.pop_back();
        heapifyDown(0);
    }

    bool isEmpty() {
        return heap.empty();
    }

    int size() {
        return heap.size();
    }

};

